mapper.select:
    	/* 스크랩 갯수 조회 */
    	SELECT COUNT(idx) as total
		FROM memopad_scrap
		WHERE del_yn = 'N'

		AND nation_code = #{nationCode}


		AND city_idx IN

	            ${item}



		AND category_idx IN

	            ${item}


		ORDER BY reg_date DESC

/* 스크랩 조회 */ SELECT scrap.idx, scrap.nation_code, scrap.city_idx, scrap.category_idx, scrap.image_url, scrap.title, scrap.description, (SELECT nickname FROM memopad_user WHERE idx = writer) as writer, scrap.url, (SELECT COUNT(idx) FROM memopad_scrap_like WHERE scrap_idx = scrap.idx AND del_yn = 'N') as likeCount, scrap.reg_date, IF(writer = ${userIdx}, 'true', 'false') as owner, IF((SELECT COUNT(idx) FROM memopad_scrap_like WHERE user_idx = ${userIdx} AND scrap_idx = scrap.idx AND del_yn = 'N' > 0), 'true', 'false') as liked, map.idx as map_idx, map.article_idx as map_article_idx, map.article_category as map_article_category, map.title as map_title, map.latitude as map_latitude, map.longitude as map_longitude, map.reg_date as map_reg_date FROM memopad_scrap scrap LEFT JOIN ( SELECT idx, article_idx, article_category, title, latitude, longitude, del_yn, reg_date FROM memopad_map WHERE article_category = 'scrap' AND del_yn = 'N' ) map ON scrap.idx = map.article_idx WHERE scrap.del_yn = 'N'
AND scrap.nation_code = #{nationCode}
AND scrap.city_idx IN
${item}
AND scrap.category_idx IN
${item}
ORDER BY scrap.reg_date DESC LIMIT ${(page - 1) * limit}, ${limit};

mapper.update:
    	/* 스크랩 삭제 */
    	UPDATE memopad_scrap
    	SET del_yn = 'Y'
		WHERE idx = #{scrapIdx}
		AND writer = #{userIdx}

mapper.select:
    	/* 스크랩 좋아요 조회 */
    	SELECT COUNT(idx) as idx
    	FROM memopad_scrap_like
		WHERE user_idx = #{userIdx}
		AND scrap_idx = #{scrapIdx}

    	/* 스크랩 좋아요 갯수 조회 */
    	SELECT COUNT(idx) as idx
    	FROM memopad_scrap_like
		WHERE scrap_idx = #{scrapIdx}
		AND del_yn = 'N'

mapper.update:
    	/* 스크랩 좋아요 사용처리 */
    	UPDATE memopad_scrap_like
    	SET del_yn = 'N'
		WHERE user_idx = #{userIdx}
		AND scrap_idx = #{scrapIdx}

    	/* 스크랩 좋아요 삭제처리 */
    	UPDATE memopad_scrap_like
    	SET del_yn = 'Y'
		WHERE user_idx = #{userIdx}
		AND scrap_idx = #{scrapIdx}
